# Problem 8
library(plyr)
library(dplyr)

ser = ""

ser_df = data.frame(ser = unlist(strsplit(ser, "*")), stringsAsFactors = FALSE) %>%
  mutate(ser_num = as.integer(ser),
         ser_log = ifelse(ser_num %in% c(0, 1), log(ser_num + .1), log(ser_num)))

ser_df1 = ser_df %>%
  mutate(ser_log_sum = ser_log 
         + lead(ser_log, 1)
         + lead(ser_log, 2)
         + lead(ser_log, 3)
         + lead(ser_log, 4)
         + lead(ser_log, 5)
         + lead(ser_log, 6)
         + lead(ser_log, 7)
         + lead(ser_log, 8)
         + lead(ser_log, 9)
         + lead(ser_log, 10)
         + lead(ser_log, 11)
         + lead(ser_log, 12)
         + lead(ser_log, 13),
         ser_prod = ser_num 
         * lead(ser_num, 1)
         * lead(ser_num, 2)
         * lead(ser_num, 3)
         * lead(ser_num, 4)
         * lead(ser_num, 5)
         * lead(ser_num, 6)
         * lead(ser_num, 7)
         * lead(ser_num, 8)
         * lead(ser_num, 9)
         * lead(ser_num, 10)
         * lead(ser_num, 11)
         * lead(ser_num, 12)
         * lead(ser_num, 13))

st_ind = which.max(ser_df1[, 4])
st_ind1 = which.max(ser_df1[, 5])

op = ser_df1[st_ind:(st_ind+12), 2] 
op1 = ser_df1[st_ind1:(st_ind1+12), 2] 

prod(op)
prod(op1)

